sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

// Instantiate the NeedItUtils class.  Call the isDatePast method and pass 
	// the u_when_needed value.
	var niutils = new NeedItUtils();
	var isPast = niutils.isDatePast(current.u_when_needed);

	// If the isDatePast method returns true, the date is in the past.
	if(isPast == true){
		gs.addErrorMessage("From Script Include 'NeedItUtils': When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}	

	// isDateToday
	var isToday = niutils.isDateToday(current.u_when_needed);
	// If the isDateToday method returns true, the date is egal à Today.
	if(isToday == true){
		gs.addErrorMessage("From Script Include 'NeedItUtils': La date souhaitée ne peut être égale à la date du jour. Votre demande ne sera pas suvegardée.");
		current.setAbortAction(true);
	}	

/*  On remplace ce code pour appeler un script include   
	// rightnow stores the current time
    var rightnow = new GlideDateTime();
    var DateNow = rightnow.getLocalDate();

    // Create a GlideDateTime object for the When needed date
    var whenNeeded = new GlideDateTime(current.u_when_needed);
    var DateNeeded = whenNeeded.getLocalDate();

    // If the When needed date is before rightnow, do not write the record to the database
    // Output an error message to the screen
    if (whenNeeded.before(rightnow)) {
        gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
        current.setAbortAction(true);
    }

    // On compare si la date souhaitée est la même que celle du jour
    if (DateNeeded.equals(DateNow)) {
        gs.addErrorMessage("La date souhaitée ne peut être égale à la date du jour. Votre demande ne sera pas suvegardée");
        current.setAbortAction(true);
    } */

})(current, previous);
